(function(exports) {

    exports.error = function(xhr) {
    	var res = {};
		if(xhr && xhr.statusText.length > 0) {
			res.message = xhr.responseText;
			res.statusCode = xhr.status;
			res.error = xhr.statusText;
		} else {
			res.message = "Unknown Error";
			res.statusCode = 999;
			res.error = "UNKNOWN";
		}
		toastr.error(JSON.parse(res.message).message, res.statusCode + ":  " + res.error);
    };

})(typeof exports === 'undefined'? this['handler']={}: exports);
